import React from "react";
import * as Haptics from "expo-haptics";
import { Pressable, StyleSheet, useColorScheme, View, Text } from "react-native";
import ReanimatedSwipeable from "react-native-gesture-handler/ReanimatedSwipeable";
import Animated, {
  configureReanimatedLogger,
  FadeIn,
  SharedValue,
  useAnimatedStyle,
} from "react-native-reanimated";
import Reanimated from "react-native-reanimated";
import { appleRed, borderColor } from "@/constants/Colors";
import { IconCircle } from "./IconCircle";
import { IconSymbol } from "./IconSymbol";

configureReanimatedLogger({ strict: false });

interface ListItemProps {
  listId: string;
  onDelete: () => void;
}

export default function ListItem({ listId, onDelete }: ListItemProps) {
  const colorScheme = useColorScheme();
  const isDark = colorScheme === "dark";

  const RightAction = (
    prog: SharedValue<number>,
    drag: SharedValue<number>
  ) => {
    const styleAnimation = useAnimatedStyle(() => ({
      transform: [{ translateX: drag.value + 200 }],
    }));

    return (
      <Pressable
        onPress={() => {
          if (process.env.EXPO_OS === "ios") {
            Haptics.notificationAsync(Haptics.NotificationFeedbackType.Error);
          }
          console.log("delete");
          onDelete(); // Call the onDelete callback
        }}
      >
        <Reanimated.View style={[styleAnimation, styles.rightAction]}>
          <IconSymbol name="trash.fill" size={24} color="white" />
        </Reanimated.View>
      </Pressable>
    );
  };

  return (
    <Animated.View entering={FadeIn}>
      <ReanimatedSwipeable
        key={listId}
        friction={2}
        enableTrackpadTwoFingerGesture
        rightThreshold={40}
        renderRightActions={RightAction}
        overshootRight={false}
        enableContextMenu
      >
        <View style={styles.listItemContainer}>
          <Text style={[styles.listItemText, { color: isDark ? "#FFFFFF" : "#000000" }]}>{listId}</Text>
        </View>

      </ReanimatedSwipeable>
    </Animated.View>
  );
}

